import { useState } from "react";

const SimpleInput = (props) => {
  const [name, setName] = useState("");
  const [nameIsTouched, setNameIsTouched] = useState(false);
  const [email, setEmail] = useState('')
  const [emailIsTouched, setEmailIsTouched] = useState(false)

  const nameIsValid = name.trim() !== "";
  const nameIsInvalid = !nameIsValid && nameIsTouched;

  const emailIsValid = email.trim() !== ""
  const emailIsInvalid = !emailIsValid && emailIsTouched

  let formIsValid = false

  if (name && email) {
    formIsValid = true
  }

  function handleChange(event) {
    setName(event.target.value);
    setEmail(event.target.value)
  }

  function nameInputBlurHandler() {
    setNameIsTouched(true);
  }
  function emaileInputBlurHandler() {
    setEmailIsTouched(true);
  }

  function handleSubmit(event) {
    event.preventDefault();
    setNameIsTouched(true);

    if (!nameIsValid || !emailIsInvalid) {
      return;
    }
    console.log(name);
    setName("");
    setEmail('')
    setNameIsTouched(false)
    setEmailIsTouched(false)
  }

  const nameInputClass = nameIsInvalid
    ? "form-control invalid"
    : "form-control";

    const emailInputClass = emailIsInvalid ? "form-control invalid" : "form-control"

  return (
    <form onSubmit={handleSubmit}>
      <div className={nameInputClass}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={handleChange}
          value={name}
          onBlur={nameInputBlurHandler}
        />
        {nameIsInvalid && <p className="error-text">Name must not be empty.</p>}
      </div>
      <div className={emailInputClass}>
        <label htmlFor="name">Email Address</label>
        <input
          type="email"
          id="email"
          onChange={handleChange}
          value={name}
          onBlur={emaileInputBlurHandler}
        />
        {emailIsInvalid && <p className="error-text">Email must not be empty.</p>}
      </div>
      <div className="form-actions">
        <button type="submit">Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
